/* eslint-disable require-jsdoc */

import {find} from 'lodash';

const MeetingInfoCollection = {
  meetingInfos: {},

  get(id) {
    if (this.meetingInfoList[id]) {
      return this.meetingInfoList[id];
    }

    return find(
      this.meetingInfoList,
      (info) => info.sipUrl === id || info.locusId === id || info.userId === id || info.meetingLink === id
    );
  },

  set(id, info) {
    this.meetingInfos[id] = info;
    return this.meetingInfos[id];
  },

  list() {
    return this.meetingInfos;
  }
};

export default MeetingInfoCollection;
