import {pickBy} from 'lodash';

class MeetingStore {
  constructor() {
    this.meetings = {};
  }

  get(meetingId) {
    const meeting = this.meetings[meetingId];
    return meeting;
  }

  add(meeting) {
    this.meetings[meeting.id] = meeting;
  }

  list(options) {
    if (options.active) {
      return pickby(this.meetings, (value) => value.active);
    }

    return this.meetings;
  }
}

export default new MeetingStore();
